const IsValidForWhatsapp = function (id){
  if (typeof(id) === "undefined" || id === null) return false;
  if (id.endsWith('@s.whatsapp.net')) return true;
  if (id.endsWith('@g.us')) return true;
  if (id.endsWith('@lid')) return true;
  return false;
}

const response = [];
for (const item of $input.all()) {
  const body = item.json.body;
  if (body) {     

    // obsolete, marked for remove, 2024/04/25
    const token = item.json.extra.token;

    // default extra content
    const extra = item.json.extra;

    let chatId = body.conversation.meta?.sender?.identifier;    
    if (!IsValidForWhatsapp(chatId))
    {
      chatId = body.conversation.meta?.sender?.custom_attributes?.quepasa;
      if (!chatId) {
        chatId = body.conversation.meta?.sender?.phone_number;
      }
    }

    // new aproach
    if (body.content && body.id) {
      const payload = {};
      payload.chatid = chatId;
      payload.conversationid = body.conversation.id;
      payload.inreply = body.content_attributes?.in_reply_to_external_id;

      if (!payload.inreply && body.content_attributes?.in_reply_to)
        payload.inreply = `${extra.account}-${extra.inbox}-${body.content_attributes.in_reply_to}`;

      if (body.content_type === 'integrations' && body.content_attributes?.type === 'dyte')      
        body.content += '\r\n *** https://app.dyte.in/meeting/stage/' + body.content_attributes?.data.room_name;      

      // leading with \ at SHIFT+ENTER
      if (body.content)       
      {
        body.content = body.content.replace(/\n\\/g,"\n").replace(/ \\/g,"");
        payload.content = body.content; 
      }

      // should create an extra message for caption ?
      const textmsg = (!body.attachments) || (body.attachments.length > 1);

      if (body.content && textmsg) {                
        payload.messageid = `${body.id}`; // ensure as string 

        const sender = body.sender?.available_name || body.sender?.name || $env['C8Q_UNKNOWN_SENDER'] || 'Auto Atendimento';

        const customs = body.conversation.meta?.sender?.custom_attributes;
        const pat = !((customs?.hasOwnProperty('skipagenttitle') ?? false) && (customs?.skipagenttitle ?? false));

        if (pat) payload.sender = sender;

        // appending to response as new item
        response.push({ payload: { ...payload }, extra: extra });
        payload.content = undefined;
      }

      if (body.attachments) {
        let counter = 0;
        for (const attach of body.attachments) 
        {
          // fixed payload counter for multiples attachs, 2024/05/06
          const attachmentPayload = { ...payload }; // Criando uma nova inst√¢ncia de payload
          attachmentPayload.messageid = `${body.id}-A${counter++}`; // ensure as string, adding an attach counter          
          attachmentPayload.attachment = attach.data_url;

          // appending to response as new item
          response.push({ payload: attachmentPayload, extra: extra });                   
        }
      }  

      continue;
    }    

    // for each message, *not common to be more than one
    for (const message of body.conversation.messages) 
    {  
      const payload = {};
      payload.chatid = chatId;
      payload.conversationid = body.conversation.id ?? message.conversation_id;
      payload.inreply = message.content_attributes?.in_reply_to_external_id;

      if (!payload.inreply && message.content_attributes?.in_reply_to)
        payload.inreply = `${extra.account}-${extra.inbox}-${message.content_attributes.in_reply_to}`;

      // obsolete, marked for remove, 2024/04/25
      payload.token = token;

      // -------------------------------------

      if (message.content_type === 'integrations' && message.content_attributes?.type === 'dyte')      
        message.content += '\r\n *** https://app.dyte.in/meeting/stage/' + message.content_attributes?.data.room_name;      

      // leading with \ at SHIFT+ENTER
      if (message.content)       
      {
        message.content = message.content.replace(/\n\\/g,"\n").replace(/ \\/g,"");
        payload.content = message.content; 
      }

      // should create an extra message for caption ?
      const textmsg = (!message.attachments) || (message.attachments.length > 1);

      if (message.content && textmsg) {                
        payload.messageid = `${message.id}`; // ensure as string 

        const sender = message.sender?.available_name || message.sender?.name || $env['C8Q_UNKNOWN_SENDER'] || 'Auto Atendimento';

        const customs = body.conversation.meta?.sender?.custom_attributes;
        const pat = !((customs?.hasOwnProperty('skipagenttitle') ?? false) && (customs?.skipagenttitle ?? false));

        if (pat) payload.sender = sender;

        // appending to response as new item
        response.push({ payload: { ...payload }, extra: extra });
        payload.content = undefined;
      }

      if (message.attachments) {
        let counter = 0;
        for (const attach of message.attachments) 
        {
          // fixed payload counter for multiples attachs, 2024/05/06
          const attachmentPayload = { ...payload }; // Criando uma nova inst√¢ncia de payload
          attachmentPayload.messageid = `${message.id}-A${counter++}`; // ensure as string, adding an attach counter          
          attachmentPayload.attachment = attach.data_url;

          // appending to response as new item
          response.push({ payload: attachmentPayload, extra: extra });                   
        }
      }  
    }
  }
}

return response;

function hex2a(hexx) {
    var hex = hexx.toString(); //force conversion
    var str = '';
    for (var i = 0; i < hex.length; i += 2)
        str += String.fromCharCode(parseInt(hex.substr(i, 2), 16));
    return str;
}

for(let index in items) 
{ 
    if(items[index].json?.chatid){
        continue;
    }

    let body = items[index].json["body"];
    if(body) 
    {
        // trying to get from external identifier
        let chatid = body.meta?.sender?.identifier;
        if(!chatid)
        {
            // trying to get from quepasa custom property
            chatid = body.meta?.sender?.custom_attributes?.quepasa;            
            if(!chatid)
            {
                // trying to get from e-mail
                chatid = body.meta?.sender?.email;            
                if(!chatid)
                {
                    // trying to unhex from source_id
                    if(body.contact_inbox?.source_id && body.contact_inbox.source_id.includes("@")){
                        chatid = hex2a(body.contact_inbox.source_id)
                    }

                    if(!chatid)
                    {                    
                        // trying to get from phone number 
                        chatid = body.meta?.sender?.phone_number;
                        if (chatid) { 
                          chatid += '@s.whatsapp.net';
                          if (chatid.startsWith('+')) chatid = chatid.substring(1);
                        }
                    }
                }
            }
        }
        items[index].json.chatid = chatid;
    }
}

return items;
